import React from "react";
import { motion } from "framer-motion";
import styles from "./HeroSection.css";
import heroVideo from "./assets/winter-bg.mp4"; // –ü—É—Ç—å –∫ –≤–∞—à–µ–º—É –≤–∏–¥–µ–æ

const HeroSection = () => {
  return (
    <section className={styles.hero}>
      {/* –§–æ–Ω–æ–≤–æ–µ –≤–∏–¥–µ–æ */}
      <video autoPlay loop muted playsInline className={styles.videoBg}>
        <source src={heroVideo} type="video/mp4" />
        –í–∞—à –±—Ä–∞—É–∑–µ—Ä –Ω–µ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç –≤–∏–¥–µ–æ.
      </video>

      {/* –ó–∞—Ç–µ–º–Ω–µ–Ω–∏–µ —Ñ–æ–Ω–∞ */}
      <div className={styles.overlay}></div>

      {/* –ö–æ–Ω—Ç–µ–Ω—Ç */}
      <div className={styles.content}>
        <motion.h1
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className={styles.title}
        >
          –°—Ç—Ä–æ–∏—Ç–µ–ª—å—Å—Ç–≤–æ —Ç–µ–ø–ª—ã—Ö –¥–æ–º–æ–≤ –ø–æ–¥ –∫–ª—é—á –≤ –¢—é–º–µ–Ω–∏:
          <br />
          <span className={styles.highlight}>
            –ü–µ—Ä–µ–µ–∑–∂–∞–π—Ç–µ —á–µ—Ä–µ–∑ 6 –º–µ—Å—è—Ü–µ–≤! ‚ùÑÔ∏èüè°
          </span>
        </motion.h1>

        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.3, duration: 0.8 }}
          className={styles.subtitle}
        >
          –ì–∞—Ä–∞–Ω—Ç–∏—Ä—É–µ–º —Ñ–∏–∫—Å–∏—Ä–æ–≤–∞–Ω–Ω—É—é —Ü–µ–Ω—É –≤ –¥–æ–≥–æ–≤–æ—Ä–µ, —É—Å–∏–ª–µ–Ω–Ω–æ–µ —É—Ç–µ–ø–ª–µ–Ω–∏–µ –¥–ª—è
          —Å–∏–±–∏—Ä—Å–∫–∏—Ö –∑–∏–º –∏ 12 –ª–µ—Ç –Ω–∞ —Å—Ç–µ–Ω—ã –∏ —Ñ—É–Ω–¥–∞–º–µ–Ω—Ç.
        </motion.p>

        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.6, duration: 0.5 }}
          className={styles.buttons}
        >
          <button className={`${styles.btn} ${styles.btnPrimary}`}>
            –†–∞—Å—Å—á–∏—Ç–∞—Ç—å —Å—Ç–æ–∏–º–æ—Å—Ç—å –∑–∞ 2 –º–∏–Ω—É—Ç—ã
          </button>
          <button className={`${styles.btn} ${styles.btnOutline}`}>
            –°–º–æ—Ç—Ä–µ—Ç—å –ø—Ä–æ–µ–∫—Ç—ã —Å –∑–∏–º–Ω–µ–π —Ñ–æ—Ç–æ—Å—ä–µ–º–∫–æ–π
          </button>
        </motion.div>
      </div>

      {/* –ê–Ω–∏–º–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ —Å–Ω–µ–∂–∏–Ω–∫–∏ */}
      <div className={styles.snowflakes}>
        {[...Array(30)].map((_, i) => (
          <div key={i} className={styles.snowflake}></div>
        ))}
      </div>
    </section>
  );
};

export default HeroSection;
