import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import "./HeroSection.css";
import winterImage from "../../images/main.jpg";

const HeroSection = () => {
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    const img = new Image();
    img.src = winterImage;
    img.onload = () => setLoaded(true);
  }, []);

  return (
    <section className={`hero-container ${loaded ? "loaded" : ""}`}>
      {/* Фоновый слой с параллакс-эффектом */}
      <div className="hero-background">
        <div
          className="hero-image"
          style={{ backgroundImage: `url(${winterImage})` }}
        />
        <div className="hero-gradient"></div>
      </div>

      {/* Основной контент */}
      <div className="hero-content">
        <motion.div
          className="text-block"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <h1 className="main-title">
            Строительство теплых домов под ключ в Тюмени:
            <span className="highlight">Переезжайте через 6 месяцев! ❄️🏡</span>
          </h1>

          <p className="subtitle">
            Гарантируем фиксированную цену в договоре, усиленное утепление для
            сибирских зим и 12 лет на стены и фундамент.
          </p>
        </motion.div>

        {/* Кнопки с анимацией */}
        <motion.div
          className="button-group"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.3, duration: 0.8 }}
        >
          <button className="primary-btn">
            Рассчитать стоимость за 2 минуты
          </button>
          <button className="secondary-btn">
            Смотреть проекты с зимней фотосъемкой
          </button>
        </motion.div>

        {/* Дополнительные фичи */}
        <motion.div
          className="features-grid"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.6, duration: 0.8 }}
        >
          <div className="feature-card">
            <div className="feature-icon">🧊</div>
            <div className="feature-text">-50°C комфорт</div>
          </div>
          <div className="feature-card">
            <div className="feature-icon">⏱️</div>
            <div className="feature-text">6 месяцев</div>
          </div>
          <div className="feature-card">
            <div className="feature-icon">🛡️</div>
            <div className="feature-text">12 лет гарантии</div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default HeroSection;
