import React, { useRef, useEffect } from "react";
import { gsap } from "gsap";
import "./HeroSection.css";
import houseImage from "../../images/main.jpg";

const HeroSection = () => {
  const sectionRef = useRef();
  const imageRef = useRef();
  const titleRef = useRef();
  const badgeRef = useRef();

  useEffect(() => {
    // Анимация появления
    gsap.from([titleRef.current, imageRef.current, badgeRef.current], {
      opacity: 0,
      y: 50,
      duration: 1.5,
      stagger: 0.3,
      ease: "power3.out",
    });

    // Параллакс эффект
    gsap.to(imageRef.current, {
      y: 100,
      scrollTrigger: {
        trigger: sectionRef.current,
        start: "top top",
        end: "bottom top",
        scrub: true,
      },
    });
  }, []);

  return (
    <section ref={sectionRef} className="ultra-hero">
      {/* 3D эффект фона */}
      <div className="hero-3d-effect"></div>

      {/* Контент с нестандартным расположением */}
      <div className="hero-content-container">
        <div className="hero-text-block">
          <h1 ref={titleRef} className="hero-main-title">
            <span className="title-line">Теплые дома</span>
            <span className="title-line accent">для сибирских зим</span>
          </h1>

          <div className="hero-divider"></div>

          <p className="hero-subtitle">
            Строительство под ключ в Тюмени с гарантией 12 лет. Переезжайте
            через 6 месяцев!
          </p>
        </div>

        {/* Интерактивные кнопки */}
        <div className="hero-interactive-buttons">
          <button className="glass-button">
            <span>Рассчитать стоимость</span>
            <div className="button-hover-effect"></div>
          </button>
          <button className="outline-button">
            <span>Смотреть проекты</span>
          </button>
        </div>
      </div>

      {/* Изображение с эффектом */}
      <div ref={imageRef} className="hero-image-wrapper">
        <div
          className="hero-main-image"
          style={{ backgroundImage: `url(${houseImage})` }}
        >
          <div className="image-reflections"></div>
        </div>
        <div ref={badgeRef} className="floating-badge">
          <span>12 лет</span>
          <span>гарантии</span>
        </div>
      </div>

      {/* Декоративные элементы */}
      <div className="decorative-elements">
        <div className="decor-circle decor-1"></div>
        <div className="decor-circle decor-2"></div>
        <div className="decor-line"></div>
      </div>
    </section>
  );
};

export default HeroSection;
