import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import "./HeroSection.css";
import houseImage from "./assets/modern-house.jpg";

gsap.registerPlugin(ScrollTrigger);

const HeroSection = () => {
  const sectionRef = useRef();
  const imageRef = useRef();
  const titleRef = useRef();
  const subtitleRef = useRef();

  useEffect(() => {
    // Анимация появления
    gsap.fromTo(
      [titleRef.current, subtitleRef.current, imageRef.current],
      { opacity: 0, y: 50 },
      {
        opacity: 1,
        y: 0,
        duration: 1.2,
        stagger: 0.2,
        ease: "power3.out",
      }
    );

    // Параллакс эффект
    gsap.to(imageRef.current, {
      y: 100,
      scrollTrigger: {
        trigger: sectionRef.current,
        start: "top top",
        end: "bottom top",
        scrub: true,
      },
    });
  }, []);

  return (
    <section ref={sectionRef} className="modern-hero">
      <div className="hero-grid">
        {/* Левая колонка - текст */}
        <div className="text-column">
          <div className="content-wrapper">
            <h1 ref={titleRef} className="hero-title">
              <span className="title-line">Строительство теплых домов</span>
              <span className="title-line accent">под ключ в Тюмени</span>
              <span className="title-line">Переезжайте через 6 месяцев!</span>
            </h1>

            <p ref={subtitleRef} className="hero-subtitle">
              Гарантируем фиксированную цену в договоре, усиленное утепление для
              сибирских зим и 12 лет на стены и фундамент.
            </p>

            <div className="button-group">
              <button className="cta-button primary">
                <span>Рассчитать стоимость за 2 минуты</span>
                <div className="hover-effect"></div>
              </button>
              <button className="cta-button secondary">
                <span>Смотреть проекты с зимней фотосъемкой</span>
              </button>
            </div>
          </div>
        </div>

        {/* Правая колонка - изображение */}
        <div className="image-column">
          <div
            ref={imageRef}
            className="hero-image"
            style={{ backgroundImage: `url(${houseImage})` }}
          >
            <div className="image-overlay"></div>
            <div className="image-badge">
              <span>12 лет</span>
              <span>гарантии</span>
            </div>
          </div>
        </div>
      </div>

      {/* Декоративные элементы */}
      <div className="decor-circle"></div>
      <div className="decor-line"></div>
    </section>
  );
};

export default HeroSection;
