import React, { useEffect, useRef } from "react";
import { motion, useAnimation } from "framer-motion";
import "./HeroSection.css";
import heroImage from "./assets/luxury-home.jpg";

const HeroSection = () => {
  const controls = useAnimation();
  const sectionRef = useRef();

  useEffect(() => {
    const loadImage = () => {
      const img = new Image();
      img.src = heroImage;
      img.onload = () => {
        controls.start({
          opacity: 1,
          transition: { duration: 1.5 },
        });
        sectionRef.current.classList.add("loaded");
      };
    };

    loadImage();
  }, [controls]);

  return (
    <motion.section
      ref={sectionRef}
      className="luxury-hero"
      initial={{ opacity: 0 }}
      animate={controls}
    >
      {/* Фоновый контейнер с фиксированным размером */}
      <div className="hero-bg-container">
        <div
          className="hero-bg-image"
          style={{ backgroundImage: `url(${heroImage})` }}
        />
        <div className="hero-bg-overlay"></div>
      </div>

      {/* Основной контент с абсолютным позиционированием */}
      <div className="hero-content-wrapper">
        <div className="hero-content">
          <motion.div
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3, duration: 1 }}
            className="hero-text-block"
          >
            <div className="hero-badge">Эксклюзивные проекты</div>
            <h1 className="hero-title">
              <span>Теплые дома</span>
              <span className="hero-title-accent">для сибирских зим</span>
            </h1>
            <p className="hero-subtitle">
              Строительство по европейским стандартам
              <br />с гарантией 15 лет на все конструкции
            </p>
          </motion.div>

          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.6, duration: 0.8 }}
            className="hero-cta-block"
          >
            <button className="hero-cta-btn">
              <span>Рассчитать стоимость</span>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                <path
                  d="M5 12H19M19 12L12 5M19 12L12 19"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                />
              </svg>
            </button>
          </motion.div>
        </div>
      </div>

      {/* Декоративные элементы */}
      <div className="hero-decoration-line"></div>
    </motion.section>
  );
};

export default HeroSection;
