import React, { useRef, useEffect } from "react";
import { gsap } from "gsap";
import "./HeroSection.css";
import winterImage from "../../images/main.jpg";

const HeroSection = () => {
  const sectionRef = useRef();
  const titleRef = useRef();
  const subtitleRef = useRef();
  const button1Ref = useRef();
  const button2Ref = useRef();

  useEffect(() => {
    const tl = gsap.timeline();
    tl.fromTo(
      titleRef.current,
      { y: 50, opacity: 0 },
      { y: 0, opacity: 1, duration: 0.8, ease: "power3.out" }
    )
      .fromTo(
        subtitleRef.current,
        { y: 30, opacity: 0 },
        { y: 0, opacity: 1, duration: 0.6, ease: "power2.out" },
        "-=0.4"
      )
      .fromTo(
        [button1Ref.current, button2Ref.current],
        { y: 20, opacity: 0 },
        { y: 0, opacity: 1, duration: 0.5, stagger: 0.1, ease: "power2.out" },
        "-=0.3"
      );
  }, []);

  return (
    <section ref={sectionRef} className="modern-hero">
      <div className="hero-background">
        <div
          className="hero-image"
          style={{ backgroundImage: `url(${winterImage})` }}
        />
        <div className="hero-overlay"></div>
      </div>

      <div className="hero-content">
        <h1 ref={titleRef} className="hero-title">
          <span>–°—Ç—Ä–æ–∏—Ç–µ–ª—å—Å—Ç–≤–æ —Ç–µ–ø–ª—ã—Ö –¥–æ–º–æ–≤</span>
          <span className="title-accent">–ø–æ–¥ –∫–ª—é—á –≤ –¢—é–º–µ–Ω–∏</span>
          <span>–ü–µ—Ä–µ–µ–∑–∂–∞–π—Ç–µ —á–µ—Ä–µ–∑ 6 –º–µ—Å—è—Ü–µ–≤! ‚ùÑÔ∏èüè°</span>
        </h1>

        <p ref={subtitleRef} className="hero-subtitle">
          –ì–∞—Ä–∞–Ω—Ç–∏—Ä—É–µ–º —Ñ–∏–∫—Å–∏—Ä–æ–≤–∞–Ω–Ω—É—é —Ü–µ–Ω—É –≤ –¥–æ–≥–æ–≤–æ—Ä–µ, —É—Å–∏–ª–µ–Ω–Ω–æ–µ —É—Ç–µ–ø–ª–µ–Ω–∏–µ –¥–ª—è
          —Å–∏–±–∏—Ä—Å–∫–∏—Ö –∑–∏–º –∏ 12 –ª–µ—Ç –Ω–∞ —Å—Ç–µ–Ω—ã –∏ —Ñ—É–Ω–¥–∞–º–µ–Ω—Ç.
        </p>

        <div className="hero-buttons">
          <button ref={button1Ref} className="hero-button primary">
            –†–∞—Å—Å—á–∏—Ç–∞—Ç—å —Å—Ç–æ–∏–º–æ—Å—Ç—å –∑–∞ 2 –º–∏–Ω—É—Ç—ã
          </button>
          <button ref={button2Ref} className="hero-button outline">
            –°–º–æ—Ç—Ä–µ—Ç—å –ø—Ä–æ–µ–∫—Ç—ã —Å –∑–∏–º–Ω–µ–π —Ñ–æ—Ç–æ—Å—ä–µ–º–∫–æ–π
          </button>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
