import React, { useEffect, useState } from "react";
import "./HeroSection.css";
import { motion } from "framer-motion";
import winterHouse from "../../images/main.jpg";

const HeroSection = () => {
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    const img = new Image();
    img.src = winterHouse;
    img.onload = () => setLoaded(true);
  }, []);

  return (
    <section className={`hero-section ${loaded ? "loaded" : ""}`}>
      <div className="hero-background">
        <div
          className="hero-image"
          style={{ backgroundImage: `url(${winterHouse})` }}
        />
        <div className="hero-overlay"></div>
      </div>

      <div className="hero-content">
        <motion.h1
          className="hero-title"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          Строительство теплых домов под ключ в Тюмени:
          <motion.span
            className="hero-title-accent"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.3, duration: 0.8 }}
          >
            Переезжайте через 6 месяцев! ❄️🏡
          </motion.span>
        </motion.h1>

        <motion.p
          className="hero-subtitle"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.6, duration: 0.8 }}
        >
          Гарантируем фиксированную цену в договоре, усиленное утепление для
          сибирских зим и 12 лет на стены и фундамент.
        </motion.p>

        <motion.div
          className="hero-buttons"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.9, duration: 0.8 }}
        >
          <button className="hero-btn hero-btn-primary">
            Рассчитать стоимость за 2 минуты
          </button>
          <button className="hero-btn hero-btn-secondary">
            Смотреть проекты с зимней фотосъемкой
          </button>
        </motion.div>
      </div>

      <div className="hero-features">
        <div className="feature-item">
          <div className="feature-icon">🧊</div>
          <div className="feature-text">Выдерживает -50°C</div>
        </div>
        <div className="feature-item">
          <div className="feature-icon">⏱️</div>
          <div className="feature-text">6 месяцев срок</div>
        </div>
        <div className="feature-item">
          <div className="feature-icon">🛡️</div>
          <div className="feature-text">12 лет гарантии</div>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
