import React, { useEffect, useState } from "react";
import "./HeroSection.css";
import heroImage from "./assets/luxury-home.jpg";

const HeroSection = () => {
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    const img = new Image();
    img.src = heroImage;
    img.onload = () => setLoaded(true);
  }, []);

  return (
    <section className={`hero-section ${loaded ? "loaded" : ""}`}>
      <div className="hero-background">
        <div
          className="hero-image"
          style={{ backgroundImage: `url(${loaded ? heroImage : ""})` }}
        />
        <div className="hero-overlay"></div>
      </div>

      <div className="hero-content">
        <div className="hero-text-container">
          <div className="hero-badge">Премиум строительство</div>
          <h1 className="hero-title">
            <span className="hero-title-line">Теплые дома</span>
            <span className="hero-title-line accent">для сурового климата</span>
          </h1>
          <p className="hero-subtitle">
            Индивидуальные проекты с гарантией 15 лет
            <br />и энергоэффективностью класса А+
          </p>
        </div>

        <div className="hero-actions">
          <button className="hero-button primary">
            <span>Запросить консультацию</span>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
              <path
                d="M5 12H19M19 12L12 5M19 12L12 19"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              />
            </svg>
          </button>
        </div>

        <div className="hero-features">
          <div className="feature-item">
            <div className="feature-number">15</div>
            <div className="feature-text">лет гарантии</div>
          </div>
          <div className="feature-item">
            <div className="feature-number">42+</div>
            <div className="feature-text">реализованных проекта</div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
