import React, { useRef, useEffect } from "react";
import { gsap } from "gsap";
import "./HeroSection.css";
import mainImage from "../../images/main.jpg";

const HeroSection = () => {
  const sectionRef = useRef();
  const titleRef = useRef();
  const imageRef = useRef();

  useEffect(() => {
    gsap.from(titleRef.current, {
      y: 50,
      opacity: 0,
      duration: 1,
      ease: "power3.out",
    });

    gsap.from(imageRef.current, {
      scale: 1.2,
      opacity: 0,
      duration: 1.5,
      ease: "power3.out",
      delay: 0.3,
    });
  }, []);

  return (
    <section ref={sectionRef} className="modern-hero">
      <div className="hero-container">
        <div className="hero-content">
          <h1 ref={titleRef} className="hero-title">
            Строительство теплых домов под ключ в Тюмени
          </h1>

          <div className="hero-divider"></div>

          <p className="hero-subtitle">
            Гарантируем фиксированную цену в договоре, усиленное утепление для
            сибирских зим
            <br />и 12 лет гарантии на стены и фундамент
          </p>

          <div className="hero-buttons">
            <button className="hero-btn primary">
              Рассчитать стоимость за 2 минуты
            </button>
            <button className="hero-btn secondary">
              Смотреть проекты с зимней фотосъемкой
            </button>
          </div>

          <div className="hero-features">
            <div className="feature-item">
              <div className="feature-icon">❄️</div>
              <div className="feature-text">Утепление 300 мм</div>
            </div>
            <div className="feature-item">
              <div className="feature-icon">⏱️</div>
              <div className="feature-text">Срок 6 месяцев</div>
            </div>
            <div className="feature-item">
              <div className="feature-icon">🛡️</div>
              <div className="feature-text">12 лет гарантии</div>
            </div>
          </div>
        </div>

        <div ref={imageRef} className="hero-image">
          <img src={mainImage} alt="Теплый дом в Тюмени" />
          <div className="image-badge">
            <span>12 лет</span>
            <span>гарантии</span>
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
