import React, { useEffect } from "react";
import { motion, useAnimation } from "framer-motion";
import "./HeroSection.css";

const HeroSection = () => {
  const controls = useAnimation();

  useEffect(() => {
    const sequence = async () => {
      await controls.start({ opacity: 1, y: 0 });
      await controls.start({
        scale: 1.05,
        transition: { duration: 6, repeat: Infinity, repeatType: "reverse" },
      });
    };
    sequence();
  }, [controls]);

  return (
    <section className="luxury-hero">
      {/* Фоновое изображение с параллакс-эффектом */}
      <motion.div
        className="luxury-hero-bg"
        initial={{ scale: 1.1 }}
        animate={controls}
      />

      {/* Градиентное затемнение */}
      <div className="luxury-overlay"></div>

      {/* Блестящие частицы */}
      <div className="luxury-particles">
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            className="luxury-particle"
            style={{
              top: `${Math.random() * 100}%`,
              left: `${Math.random() * 100}%`,
              animationDuration: `${15 + Math.random() * 20}s`,
              animationDelay: `${Math.random() * 5}s`,
              width: `${2 + Math.random() * 3}px`,
              height: `${2 + Math.random() * 3}px`,
            }}
          />
        ))}
      </div>

      {/* Контент */}
      <div className="luxury-content">
        {/* Логотип в углу */}
        <div className="luxury-logo">
          <span className="luxury-logo-main">Thermo</span>
          <span className="luxury-logo-sub">Villa</span>
        </div>

        {/* Основной заголовок */}
        <motion.div
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3, duration: 1 }}
          className="luxury-heading"
        >
          <h1>
            <span className="luxury-title-line">Элитные теплые дома</span>
            <span className="luxury-title-highlight">в сердце Сибири</span>
          </h1>
          <div className="luxury-title-decoration"></div>
        </motion.div>

        {/* Подзаголовок */}
        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.6, duration: 1 }}
          className="luxury-subtitle"
        >
          Эксклюзивные проекты с гарантией 12 лет.
          <br />
          Технологии, проверенные при -50°C.
        </motion.p>

        {/* Кнопки */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.9, duration: 0.8 }}
          className="luxury-actions"
        >
          <button className="luxury-btn luxury-btn-primary">
            <span>Запросить каталог</span>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
              <path
                d="M5 12H19M19 12L12 5M19 12L12 19"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              />
            </svg>
          </button>
          <button className="luxury-btn luxury-btn-secondary">
            <span>3D тур по дому</span>
          </button>
        </motion.div>

        {/* Дополнительная информация */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1.2, duration: 0.8 }}
          className="luxury-features"
        >
          <div className="luxury-feature">
            <div className="luxury-feature-icon">❄️</div>
            <div className="luxury-feature-text">300 мм утеплителя</div>
          </div>
          <div className="luxury-feature">
            <div className="luxury-feature-icon">⏱️</div>
            <div className="luxury-feature-text">Срок сдачи 6 месяцев</div>
          </div>
          <div className="luxury-feature">
            <div className="luxury-feature-icon">🏆</div>
            <div className="luxury-feature-text">12 лет гарантии</div>
          </div>
        </motion.div>
      </div>

      {/* Индикатор скролла */}
      <motion.div
        className="luxury-scroll-hint"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 1.5, duration: 0.8 }}
      >
        <div className="luxury-scroll-line"></div>
      </motion.div>
    </section>
  );
};

export default HeroSection;
