import React from "react";
import { motion } from "framer-motion";
import "./HeroSection.css";
import heroVideo from "./assets/hero-video.mp4"; // –ó–∞–º–µ–Ω–∏—Ç–µ –Ω–∞ –≤–∞—à –≤–∏–¥–µ–æ—Ñ–∞–π–ª

const HeroSection = () => {
  return (
    <section className="hero">
      {/* –§–æ–Ω–æ–≤–æ–µ –≤–∏–¥–µ–æ */}
      <video autoPlay loop muted playsInline className="hero-video">
        <source src={heroVideo} type="video/mp4" />
        –í–∞—à –±—Ä–∞—É–∑–µ—Ä –Ω–µ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç –≤–∏–¥–µ–æ.
      </video>

      {/* –ó–∞—Ç–µ–º–Ω–µ–Ω–∏–µ —Ñ–æ–Ω–∞ */}
      <div className="hero-overlay"></div>

      {/* –ö–æ–Ω—Ç–µ–Ω—Ç */}
      <div className="hero-content">
        <motion.h1
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="hero-title"
        >
          –°—Ç—Ä–æ–∏—Ç–µ–ª—å—Å—Ç–≤–æ —Ç–µ–ø–ª—ã—Ö –¥–æ–º–æ–≤ –ø–æ–¥ –∫–ª—é—á –≤ –¢—é–º–µ–Ω–∏:
          <motion.span
            className="hero-highlight"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5, duration: 0.8 }}
          >
            –ü–µ—Ä–µ–µ–∑–∂–∞–π—Ç–µ —á–µ—Ä–µ–∑ 6 –º–µ—Å—è—Ü–µ–≤! ‚ùÑÔ∏èüè°
          </motion.span>
        </motion.h1>

        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.7, duration: 0.8 }}
          className="hero-subtitle"
        >
          –ì–∞—Ä–∞–Ω—Ç–∏—Ä—É–µ–º —Ñ–∏–∫—Å–∏—Ä–æ–≤–∞–Ω–Ω—É—é —Ü–µ–Ω—É –≤ –¥–æ–≥–æ–≤–æ—Ä–µ, —É—Å–∏–ª–µ–Ω–Ω–æ–µ —É—Ç–µ–ø–ª–µ–Ω–∏–µ –¥–ª—è
          —Å–∏–±–∏—Ä—Å–∫–∏—Ö –∑–∏–º –∏ 12 –ª–µ—Ç –Ω–∞ —Å—Ç–µ–Ω—ã –∏ —Ñ—É–Ω–¥–∞–º–µ–Ω—Ç.
        </motion.p>

        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1, duration: 0.5 }}
          className="hero-buttons"
        >
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="hero-btn hero-btn-primary"
          >
            –†–∞—Å—Å—á–∏—Ç–∞—Ç—å —Å—Ç–æ–∏–º–æ—Å—Ç—å –∑–∞ 2 –º–∏–Ω—É—Ç—ã
          </motion.button>
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="hero-btn hero-btn-outline"
          >
            –°–º–æ—Ç—Ä–µ—Ç—å –ø—Ä–æ–µ–∫—Ç—ã —Å –∑–∏–º–Ω–µ–π —Ñ–æ—Ç–æ—Å—ä–µ–º–∫–æ–π
          </motion.button>
        </motion.div>
      </div>

      {/* –ê–Ω–∏–º–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ —Å–Ω–µ–∂–∏–Ω–∫–∏ */}
      <div className="hero-snowflakes">
        {[...Array(30)].map((_, i) => (
          <div
            key={i}
            className="hero-snowflake"
            style={{
              left: `${Math.random() * 100}%`,
              animationDuration: `${10 + Math.random() * 20}s`,
              animationDelay: `${Math.random() * 5}s`,
              opacity: 0.5 + Math.random() * 0.5,
              width: `${5 + Math.random() * 5}px`,
              height: `${5 + Math.random() * 5}px`,
            }}
          ></div>
        ))}
      </div>
    </section>
  );
};

export default HeroSection;
